import 'package:belajar_flutter/main_layouts.dart';
import 'package:flutter/material.dart';

class LatihanRowCol extends StatelessWidget {
  const LatihanRowCol({super.key});

  Widget buildImage(String url) {
    return Container(
      margin: EdgeInsets.all(8),
      width: 100,
      height: 100,
      decoration: BoxDecoration(
        borderRadius: BorderRadius.circular(12),
        image: DecorationImage(
          image: NetworkImage(url),
          fit: BoxFit.cover,
        ),
      ),
    );
  }

  Widget buildActionButton(IconData icon, String label) {
    return Column(
      mainAxisSize: MainAxisSize.min,
      children: [
        Icon(icon, color: Colors.blueAccent),
        SizedBox(height: 4),
        Text(label),
      ],
    );
  }

  @override
  Widget build(BuildContext context) {
    return MainLayouts(
      title: 'BIGETRON E-SPORT',
      body: SingleChildScrollView(
        child: Column(
          children: [
            Row(
              mainAxisAlignment: MainAxisAlignment.center,
              children: [
                buildImage(
                  'data:image/jpeg;base64,',
                ),
              ],
            ),

            Row(
              mainAxisAlignment: MainAxisAlignment.center,
              children: [
                buildImage(
                  'https://upload.wikimedia.org/wikipedia/commons/thumb/e/e0/Clouds_over_the_Atlantic_Ocean.jpg/640px-Clouds_over_the_Atlantic_Ocean.jpg',
                ),
                buildImage(
                  'https://upload.wikimedia.org/wikipedia/commons/thumb/2/21/Sunflower_from_Silesia2.jpg/640px-Sunflower_from_Silesia2.jpg',
                ),
              ],
            ),

            Row(
              mainAxisAlignment: MainAxisAlignment.center,
              children: [
                buildImage(
                  'https://upload.wikimedia.org/wikipedia/commons/thumb/3/36/Hopetoun_falls.jpg/640px-Hopetoun_falls.jpg',
                ),
                buildImage(
                  'https://upload.wikimedia.org/wikipedia/commons/thumb/b/b6/Image_created_with_a_mobile_phone.png/640px-Image_created_with_a_mobile_phone.png',
                ),
                buildImage(
                  'https://upload.wikimedia.org/wikipedia/commons/thumb/3/3f/Fronalpstock_big.jpg/640px-Fronalpstock_big.jpg',
                ),
              ],
            ),

            SizedBox(height: 16),

            // Row tombol Call, Route, Share
            Container(
              padding: EdgeInsets.symmetric(vertical: 12),
              color: Colors.blueGrey[50],
              child: Row(
                mainAxisAlignment: MainAxisAlignment.spaceEvenly,
                children: [
                  buildActionButton(Icons.call, 'Call'),
                  buildActionButton(Icons.route, 'Route'),
                  buildActionButton(Icons.share, 'Share'),
                ],
              ),
            )
          ],
        ),
      ),
    );
  }
}
